// Enviar el primer missatge ara, i els missatges posteriors un cop al dia.
if (! prev)
 next = new Date();
else {
 var now = new Date();
 next = new Date(prev); // Copiar l'argument de la data, de manera que no la modifiquem.
 do {
 next.setDate(next.getDate() + 1);
 } while (next < now);
 // ^^^ Que no s'intenti enviar si ja ha passat la programació, en cas que Thunderbird estés inactiu al
// moment programat per enviar.
}
