// Odešle zprávu poprvé teď a dále pak jednou za den.
if (! prev)
    next = new Date();
else {
    var now = new Date();
    next = new Date(prev); // Kopie argumentu date, abychom ho neupravili.
    do {
        next.setDate(next.getDate() + 1);
    } while (next < now);
    // ^^^ Nechceme zprávu odeslat v minulosti v případě, že Thunderbird
    // spal v naplánovaném čase odeslání.
}
