// Προεπιλογές
var workDays = [1, 2, 3, 4, 5]; // Mon - Fri; Sun == 0, Sat == 6 
var workStart = [8, 30]; // Start of the work day as [H, M] 
var workEnd = [17, 30]; // End of the work day as [H, M] 
if (args && args[0])
     workDays = args[0];
if (args && args[1])
     workStart = args[1]; 
if (args && args[2])
     workEnd = args[2];
 if (prev)
     // Δεν αναμένεται σε κανονική χρήση, αλλά χρησιμοποιείται την τρέχουσα ώρα για τη δοκιμή.
     next = new Date(prev); 
else
     next = new Date();
 // Αν είμαστε πέρα από το τέλος της εργάσιμης μέρας ή όχι σε μια εργάσιμη μέρα, να μετακινηθεί η εργασία
// σε ώρα έναρξης της επόμενης ημέρας.
while ((next.getHours() > workEnd[0]) ||
        (next.getHours() == workEnd[0] && next.getMinutes() > workEnd[1]) ||
        (workDays.indexOf(next.getDay()) == -1)) {     next.setDate(next.getDate() + 1);
     next.setHours(workStart[0]);
     next.setMinutes(workStart[1]);
 } 
// If we're before the beginning of the workday, move to its start time. 
if ((next.getHours() < workStart[0]) ||
     (next.getHours() == workStart[0] && next.getMinutes() < workStart[1])) {
     next.setHours(workStart[0]);
     next.setMinutes(workStart[1]); 
}

