// Wyślij wiadomość natychmiast, oraz następnie dodatkowo raz dziennie.
if (! prev)
    next = new Date();
else {
    var now = new Date();
    next = new Date(prev); // Kopia argumentu daty bez modyfikowania go.
    do {
        next.setDate(next.getDate() + 1);
    } while (next < now);
    // ^^^ nie próbuj wysłać w przeszłości, Jeśli ThunerBird był uśpiony
    // w zaplanowanym czasie wysłania.
}
